import React from 'react';
import './App.css';
const url = "https://api.graphql.jobs/"

const cleanData = jobs => {
  return jobs.map(job => {
    if (job.cities.length === 0) job.cities[0] = 'NO_CITY';
    return job;
  })
}

const sortJobsByRecentDate = jobs => {
  return jobs.sort((a, b) => {
    return new Date(a.postedAt) - new Date(b.postedAt);
  })
}

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      jobs: [],
    }
  }

  async componentDidMount() {
    const query = `
      query {
        jobs {
          id
          title
          cities {
            name
            id
          }
          description
          company {
            id
            name
            websiteUrl
          }
          postedAt
        }
      }
    `
    const opts = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ query })
    };

    fetch(url, opts)
      .then(res => res.json())
      .then(({ data }) => {
        const jobs = cleanData(data.jobs)
        this.setState({
          jobs,
        }, console.log(this.state))
      })
      .catch(console.error);
  }

  render() {
    return (
      <div className="app">
        <div className="container">
          <div className="title">
            Jobs
          </div>
          <div className="job-content">

            <div className="job-tab">
              <div className="job-title">

              </div>
              <div className="job-description">
                <div className="job-position"></div>
                <div className="job-location"></div>
              </div>
            </div>

          </div>
          </div>
      </div>
    );
  }
}

export default App;
