import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown'
import './App.css';

const url = "https://api.graphql.jobs/"
const query = `
  query {
    jobs {
      id
      title
      cities {
        name
        id
      }
      description
      company {
        id
        name
        websiteUrl
      }
      postedAt
    }
  }
`

const cleanData = jobs => {
  return jobs.map(job => {
    if (job.cities.length === 0) {
      job.cities[0] = 'NO_CITY';
      job.citiesString = "Remote / No Location"
    }
    else if (job.cities.length === 1) {
      job.cities = [job.cities[0].name];
      job.citiesString = job.cities[0];
    }
    else {
      job.cities = job.cities.map(city => city.name)
      job.citiesString = job.cities.join(" â€¢ ")
    }
    job.postedAt = new Date(job.postedAt);
    return job;
  })
}

const sortJobsByRecentDate = jobs => {
  return jobs.sort((a, b) => {
    return b.postedAt - a.postedAt;
  })
}

const sortJobsByCity = jobs => {
  return jobs.sort((a, b) => {
    return a.citiesString - b.citiesString;
  })
}

const JobTab = ({ job, jobs }) => {
  const [renderDescription, setRenderDescription] = useState(false);
  return (
    <div className="job-tab">
      <div className="job-vis" onClick={() => setRenderDescription(!renderDescription)}>
        <div className="job-header">
        <div className="job-title">
          {job.title} â€¢ {job.company.name}
        </div>
        <div className="job-location">
          {job.citiesString} â€¢ {new Intl.DateTimeFormat("en-GB", {
            year: "numeric",
            month: "long",
            day: "2-digit"
          }).format(job.postedAt)}
        </div>
      </div>
       { renderDescription ?
        <div className="job-description">
          <ReactMarkdown>
            {job.description}
          </ReactMarkdown>
          <div className="job-contact">
            {/* todo */}
          </div>
        </div> :
        null
       }
    </div>
  </div>
)}

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      jobs: [],
      locationSearch: '',
    }
  }

  componentDidMount() {
    const opts = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ query })
    };

    fetch(url, opts)
      .then(res => res.json())
      .then(({ data }) => {
        let jobs = cleanData(data.jobs);
        jobs = sortJobsByRecentDate(jobs);
        this.setState({ jobs });
      })
      .catch(console.error);
  }

  render() {
    return (
      <div className="app">
        <div className="container">
          <div className="title">
            Job Board
          </div>
          <div className="job-filters">
            Search jobs by location {" "}
            <input className="job-location-search" value={this.state.locationSearch} onChange={this.handleChange} />
          </div>
          <div className="open-positions">
            Open Positions
          </div>
          <div className="job-content">
             { this.state.jobs.length !== 0 ?
              this.state.jobs.map(job => <JobTab job={job} jobs={this.state.jobs} />) : null }
          </div>
          </div>
      </div>
    );
  }
}

export default App;
