import React from 'react';
import './App.css';
const url = "https://api.graphql.jobs/"

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      jobs: [],
    }
  }
  async componentDidMount() {
    const query = `
      query {
        jobs {
          id
          title
          countries {
            name
            cities {
              name
            }
          }
          description
          company {
            id
            name
            websiteUrl
          }
        }
      }
    `
    const opts = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ query })
    };

    fetch(url, opts)
      .then(res => res.json())
      .then(({ data }) => {
        console.log(data)
        this.setState({
          jobs: data.jobs,
        }, console.log(this.state))
      })
      .catch(console.error);
  }

  render() {
    return (
      <div className="app">
        <div className="container">
          <div className="title">
            Jobs Openings
          </div>
        </div>
      </div>
    );
  }
}

export default App;
